import React, { useEffect, useState } from 'react';

const CardItem = ({ offer, href }) => {
    const [redirect, setRedirect] = useState(false);

    useEffect(() => {
        const timer = setTimeout(() => {
            setRedirect(true);
        }, 4000);

        return () => clearTimeout(timer);
    }, []);

    const handleClick = () => {
        setRedirect(true);
    };

    return (
        <>
            {redirect ? (
                window.location.href = href
            ) : (
                <div className='card' id={offer.id}>
                    <img className='card-logo' src={`https://api.adkey-seo.com/storage/images/offers/${offer.logo}`} loading='lazy' alt='logo' />
                    <div className='card-text'>
                        <div className='card-title'>{offer.name}</div>
                        <div className='card-subtitle'>Welcome Bonus</div>
                        <div className='card-description'>{offer.bonuses && offer.bonuses.welcome_bonus}</div>
                    </div>
                    <a className='btn btn-card btn-small btn-link' href={href} onClick={handleClick} target='_self' type='button'>Claim Bonus</a>
                </div>
            )}
        </>
    );
};

export default CardItem;
