import React from 'react';

const OffersAndLink = ({ data }) => {
  if (!data || !data.offers || data.offers.length === 0) {
    return <div>No offers available</div>;
  }

  const firstOffer = data.offers[0];

  return (
    <div>
      <h2>First Offer</h2>
      <p>Offer ID: {firstOffer.id}</p>
      <p>Sub2: {firstOffer.sub2}</p>
      <p>Logo: <img src={firstOffer.logo} alt={firstOffer.name} /></p>
      <p>Bonuses:</p>
      <ul>
        <li>Rate: {firstOffer.bonuses.rate}</li>
        <li>Amount: {firstOffer.bonuses.amount}</li>
        <li>Free Spins: {firstOffer.bonuses.free_spins}</li>
        <li>Welcome Bonus: {firstOffer.bonuses.welcome_bonus}</li>
      </ul>
      <p>Link: <a href={firstOffer.link} target="_blank" rel="noopener noreferrer">{firstOffer.link}</a></p>
    </div>
  );
};

export default OffersAndLink;
