import React from "react";
import Header from "./components/Header";
import Intro from "./components/Intro";
import CardsItems from "./components/CardsItems";
import BonusDetails from "./components/BonusDetails";
import Footer from "./components/Footer";


class App extends React.Component {

  constructor(props) {
    super(props)
    this.state = {
      items: [
        {
          id: 1,
          img: 'Gunst-Bet_logo.webp',
          imgAdditional: 'Gunst-Bet_logo.png',
          title: 'CASINO',
          subtitle: 'Welcome Bonus',
          description: '00% UP TO 1000 AUD + 100 FREE SPINS',
          bonuses: 'Cresus casino deposit bonus',
          rate: '0%',
          maxAmaunt: '100 $',
          link: '#',
          bonusValid: 'Slot machines, Roulette, Blackjack',
          wageringCondition: 'Slot machines: No wagering requirement  American Roulette: No wagering requirement',
          wager: '110% x 10',
          cashableBonus: 'No',
          cashBonus: 'No',
          category: 'fruits',
          rating: 0
        }
      ]

    }
  }

  render() {
    return (
      <div className='wrapper'>

        <Header />
        <div className='content-wrap'>
          <Intro />
          <CardsItems items={this.state.items} />
          <BonusDetails items={this.state.items} />
          <Footer />
        </div>

      </div>
    );
  }
}

export default App;
