import React, { Component } from 'react';
import CardItem from './CardItem';

export class CardsItems extends Component {
  constructor(props) {
    super(props);
    this.state = {
      showAll: false,
      buttonDisabled: false,
      itemsToShow: []
    };
  }

  componentDidMount() {
    fetch('https://api.adkey-seo.com/api/website/get-website/295')
      .then(response => {
        if (!response.ok) {
          throw new Error('Network response was not ok');
        }
        return response.json();
      })
      .then(data => {
        if (data && data.website && data.website.offers) {
          this.setState({ itemsToShow: data.website.offers });
        } else {
          throw new Error('Invalid data format');
        }
      })
      .catch(error => {
        console.error('Error fetching data:', error);
        // Обробка помилки
      });
  }

  render() {
    const { showAll, buttonDisabled, itemsToShow } = this.state;

    return (
      <div className='productCards' id='productCards'>
        <div className='container'>
          <h2 className='title h2-text'>Top Casino</h2>
          <div className='productCards-inner inner-container'>
            {showAll ? this.props.items.map((el, index) => (
              <CardItem key={index} item={el} />
            )) : itemsToShow.map((el, index) => (
              <CardItem key={index} item={el} />
            ))}
          </div>
          {!showAll && (
            <div className='button-box'>
              <button className='btn btn-large bg-violet' onClick={this.handleClick} disabled={buttonDisabled}>
                All Casino
              </button>
            </div>
          )}
        </div>
      </div>
    );
  }

  handleClick = () => {
    this.setState({ showAll: true, buttonDisabled: true });
  };
}

export default CardsItems;
