import React, { useState, useEffect } from 'react';
import GameItem from './GameItem';
import DataFetchingComponent from './DataFetchingComponent'; // Змінили імпорт

const Games = () => {
  const [games, setGames] = useState([]);

  return (
    <div className='games' id='games'>
      <div className='container'>
        <h2 className='title h2-text'>
          Top games
        </h2>
        <DataFetchingComponent render={({ data, link }) => ( // Використовуємо DataFetchingComponent для отримання даних та посилання
          <div className='games-items'>
            {games.map(game => (
              <GameItem key={game.id} id={game.id} name={game.name} image={game.image} link={link} /> // Передаємо посилання у GameItem
            ))}
            <a className='btn btn-link btn-large' href={link}>All Games</a>
          </div>
        )}/>
      </div>
    </div>
  );
};

export default Games;
