import React, { useState, useEffect } from 'react';
import CardItem from './CardItem';

const CardsItems = () => {
  const [items, setItems] = useState([]);

  useEffect(() => {
    fetch('https://api.adkey-seo.com/api/website/get-website/295')
      .then(response => {
        if (!response.ok) {
          throw new Error('Network response was not ok');
        }
        return response.json();
      })
      .then(data => {
        if (data && data.website && data.website.offers) {
          setItems(data.website.offers);
        } else {
          throw new Error('Invalid data format');
        }
      })
      .catch(error => {
        console.error('Error fetching data:', error);
        // Встановлення значення за замовчуванням або відображення повідомлення про помилку
      });
  }, []);

  return (
    <div className='card'>
            <img className='card-logo' src={`https://api.adkey-seo.com/storage/images/offers/${offer.logo}`} loading='lazy' alt='logo' />
            <div className='card-text'>
                <div className='card-title'>{offer.name}</div>
                <div className='card-subtitle'>Welcome Bonus</div>
                <div className='card-description'>{offer.bonuses && offer.bonuses.welcome_bonus}</div>
            </div>
            <a className='btn btn-card btn-small bg-violet' href={offer.link} target='_self' type='button'>Claim Bonus</a>
        </div>
  );
};

export default CardItem;
