import React, { Component } from 'react';
import CardsItems from './CardsItems';

class ParentComponent extends Component {
  constructor(props) {
    super(props);
    this.state = {
      offers: [] // Масив об'єктів, який ви отримуєте з сервера
    };
  }

  componentDidMount() {
    // Отримання даних з сервера
    fetch('https://api.adkey-seo.com/api/website/get-website/295')
      .then(response => response.json())
      .then(data => {
        const offers = data.offers; // Отримання масиву об'єктів offers
        this.setState({ offers }); // Оновлення стану з отриманими даними
      })
      .catch(error => console.error('Error fetching data:', error));
  }

  render() {
    const { offers } = this.state;

    return (
      <div>
        <h1>Parent Component</h1>
        <CardsItems offers={offers} /> {/* Передача пропсу offers у CardsItems */}
      </div>
    );
  }
}

export default ParentComponent;
