import React, { useState, useEffect } from 'react';

// Імпортуємо компонент OffersAndLink
import OffersAndLink from './OffersAndLink';

const WebsiteRequest = () => {
  const [data, setData] = useState(null);
  const [fetched, setFetched] = useState(false);

  useEffect(() => {
    const fetchData = async () => {
      try {
        const response = await fetch('https://api.adkey-seo.com/api/website/get-website/295');
        const data = await response.json();

        if (data.offers && data.offers.length > 0) {
          setData(data);
          setFetched(true);
          console.log('Отримані дані:', data); // Виведення даних у консоль
        } else {
          console.error('Поле "offers" порожнє або відсутнє в отриманих даних.');
        }
      } catch (error) {
        console.error(`Помилка при виконанні запиту: ${error.message}`);
      }
    };

    if (!fetched) {
      fetchData();
    }
  }, [fetched]);

  // Повертаємо компонент, який використовує дані з запиту
  return (
    <div>
      {data ? (
        <div>
          <h1>Отримані дані:</h1>
          {/* Використовуємо компонент OffersAndLink */}
          <OffersAndLink offers={data.offers} />
        </div>
      ) : (
        <p>Завантаження...</p>
      )}
    </div>
  );
};

export default WebsiteRequest;
