import React, { useState, useEffect } from 'react';
import CardItem from './CardItem';
import MiddlePage from './MiddlePage';

const CardsItems = () => {
  const [data, setData] = useState(null);
  const [displayedOffers, setDisplayedOffers] = useState([]);
  const [showMiddlePage, setShowMiddlePage] = useState(false); // Додано стан для визначення видимості MiddlePage
  const [showAllOffers, setShowAllOffers] = useState(false); // Додано стан для визначення, чи відображати всі пропозиції

  useEffect(() => {
    const fetchData = async () => {
      try {
        const response = await fetch('https://api.adkey-seo.com/api/website/get-website/295');
        const responseData = await response.json();
        setData(responseData);
        setDisplayedOffers(responseData.offers.slice(0, 8));
      } catch (error) {
        console.error('Error fetching data:', error);
      }
    };

    fetchData();
  }, []);

  const handleClick = () => {
    setShowAllOffers(true);
  };

  const handleCardItemClick = () => {
    setShowMiddlePage(true); // Встановлюємо showMiddlePage на true після кліку на карточку
  };

  return (
    <div className='productCards' id='productCards'>
      <div className='container'>
        <h2 className='title h2-text'>
          Top Casino's&nbsp;
          <span className='country_name'>{data?.website.country_name}</span>
        </h2>
        <div className='productCards-inner inner-container'>
          {displayedOffers.map(offer => (
            <CardItem key={offer.id} offer={offer} href={offer.link} setShowContent={handleCardItemClick} />
          ))}
        </div>
        {showMiddlePage && <MiddlePage />} {/* Показуємо MiddlePage, якщо showMiddlePage дорівнює true */}
        {!showAllOffers && (
          <div className='button-box'>
            <button className='btn btn-large' onClick={handleClick}>
              All Casino
            </button>
          </div>
        )}
      </div>
    </div>
  );
};

export default CardsItems;
