import React, { useState, useEffect } from 'react';

const BonusDetails = ({ offer }) => {
  const [link, setLink] = useState(null);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);

  useEffect(() => {
    const fetchData = async () => {
      try {
        const response = await fetch(`https://api.adkey-seo.com/api/website/get-website/${offer.website_id}`);
        const data = await response.json();

        if (data.offers && data.offers.length > 0) {
          const link = data.offers[0].link;
          setLink(link);
          setLoading(false);
        } else {
          setError('Поле "offers" порожнє або відсутнє в отриманих даних.');
          setLoading(false);
        }
      } catch (error) {
        setError(`Помилка при виконанні запиту: ${error.message}`);
        setLoading(false);
      }
    };

    fetchData();
  }, [offer.website_id]);

  return (
    <div className="bonus-card">
      <h3>{offer.name}</h3>
      {loading ? (
        <p>Loading...</p>
      ) : error ? (
        <p>{error}</p>
      ) : (
        <a href={link} className="btn btn-link">Claim Bonus</a>
      )}
    </div>
  );
};

export default BonusDetails;
