import React, { useState, useEffect } from 'react';

const DataAndExtractLink = ({ render }) => {
  const [link, setLink] = useState(null);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);

  useEffect(() => {
    const fetchData = async () => {
      try {
        const response = await fetch('https://api.adkey-seo.com/api/website/get-website/295');
        if (!response.ok) {
          if (response.status === 429) {
            // If rate limited, wait for some time before retrying
            await new Promise(resolve => setTimeout(resolve, 5000)); // Wait for 5 seconds (adjust as needed)
            return fetchData();
          } else {
            throw new Error(`HTTP error! status: ${response.status}`);
          }
        }

        const data = await response.json();

        if (data.offers && data.offers.length > 0) {
          const link = data.offers[0].link;
          setLink(link);
          setLoading(false);
        } else {
          setError("Поле 'offers' порожнє або відсутнє в отриманих даних.");
          setLoading(false);
        }
      } catch (error) {
        setError(`Помилка при виконанні запиту: ${error.message}`);
        setLoading(false);
      }
    };

    fetchData();
  }, []);

  return render({ link, loading, error });
};

export default DataAndExtractLink;
