import React, { useState, useEffect } from 'react';

const Providers = () => {
    const [providers, setProviders] = useState([]);

    useEffect(() => {
        fetch('https://api.adkey-seo.com/api/website/get-providers/')
            .then(response => response.json())
            .then(data => setProviders(data))
            .catch(error => console.error('Error fetching providers:', error));
    }, []);

    return (
        <section className='providers'>
            <div className="container">
                <h2>SOFTWARE PROVIDERS</h2>
                <div className="providers-items inner-flex">
                    {providers.map(provider => (
                        <a key={provider.id} href={provider.link} className='btn-link'>
                            <div className='provider-item'>
                                <img src={`https://api.adkey-seo.com/storage/images/providers/${provider.image}`} 
                                    alt={provider.name} title={provider.name} 
                                    loading='lazy'/>
                            </div>
                        </a>
                    ))}
                </div>
            </div>
        </section>
    );
};

export default Providers;
