import React, { useState } from 'react';
import { BrowserRouter as Router, Switch, Route } from 'react-router-dom';
import Header from './components/Header';
import Intro from './components/Intro';
import CardsItems from './components/CardsItems';
import Games from './components/Games';
import BtnLinkHandler from './components/BtnLinkHandler';
import OptionalInformation from './components/OptionalInformation';
import AdditionalInformation from './components/AdditionalInformation';
import Advantages from './components/Advantages';
import PaymentMethods from './components/PaymentMethods';
import Licences from './components/Licences';
import Providers from './components/Providers';
import Support from './components/Support';
import FAQ from './components/FAQ';
import BonusDetails from './components/BonusDetails';
import Footer from './components/Footer';
import MiddlePage from './components/MiddlePage';
import NotFound from './components/NotFound';

const App = () => {
  const [showContent, setShowContent] = useState(true);

  return (
    <Router>
      <div className='wrapper'>
        <Header />
        <div className='content-wrap'>
          <Switch>
            <Route exact path="/">
              {showContent && (
                <>
                  <Intro />
                  <CardsItems />
                  <Advantages />
                  <Games />
                  <OptionalInformation />
                  <PaymentMethods />
                  <Licences />
                  <Providers />
                  <FAQ />
                  <Support />
                  <AdditionalInformation />
                  {/* <BonusDetails/> */}
                  <Footer />
                </>
              )}
              {!showContent && <MiddlePage />}
              <BtnLinkHandler setShowContent={setShowContent} />
            </Route>
            {/* Добавляем маршрут для компонента NotFound */}
            <Route>
              <NotFound />
            </Route>
          </Switch>
        </div>
      </div>
    </Router>
  );
}

export default App;
