import React, { useState } from 'react';
import ServerDataComponent from './ServerDataComponent'; // Підставте шлях до вашого компонента

const DataDisplayComponent = () => {
  const [link, setLink] = useState(null);

  const onDataReceived = (data) => {
    if (data && data.offers && data.offers.length > 0) {
      setLink(data.offers[0].link);
    }
  };

  return (
    <div>
      <h2>Server Data:</h2>
      {link ? (
        <a href={link}>{link}</a>
      ) : (
        <p>Loading...</p>
      )}
      {/* Передаємо onDataReceived як пропс у ServerDataComponent */}
      <ServerDataComponent onDataReceived={onDataReceived} />
    </div>
  );
};

export default DataDisplayComponent;
