import React, { useState, useEffect } from 'react';

const Intro = () => {
    const [offer, setOffer] = useState(null);

    useEffect(() => {
        const fetchData = async () => {
            try {
                const response = await fetch('https://api.adkey-seo.com/api/website/get-website/295');
                const data = await response.json();
                if (data.offers && data.offers.length > 0) {
                    setOffer(data.offers[0]);
                }
            } catch (error) {
                console.error('Error fetching data:', error);
            }
        };

        fetchData();
    }, []);

    useEffect(() => {
        const handleButtonClick = (event) => {
            event.preventDefault();
            // Додаємо код для відображення проміжної сторінки

            // Затримка перед переходом на нову сторінку (можна видалити цей рядок, якщо мідл пейдж відображається миттєво)
            setTimeout(() => {
                const link = event.target.getAttribute('href');
                window.location.href = link;
            }, 1000); // Затримка у мілісекундах перед переходом (тут 1000 мілісекунд = 1 секунда)
        };

        const buttons = document.querySelectorAll('.btn-link');
        buttons.forEach(button => {
            button.addEventListener('click', handleButtonClick);
        });

        return () => {
            buttons.forEach(button => {
                button.removeEventListener('click', handleButtonClick);
            });
        };
    }, []);

    return (
        <div id='intro' className='intro'>
            <div className='container'>
                <div className='intro-content'>
                    <div className='intro-title'>
                        <h1 className='title-text h1-text'>
                            Welcome Bonus
                        </h1>
                    </div>
                    {offer && (
                        <div>
                            <div className='intro-subtitle'>
                                <div className='subtitle-text'>
                                    EXCLUSIVE WELCOME OFFER OF
                                    <br />
                                    <span>
                                        {offer.bonuses && offer.bonuses.welcome_bonus}
                                    </span>
                                </div>
                            </div>
                            <div className='button-box'>
                                <a href={offer.link} type='button' className='btn btn-large btn-link' target='_self'>
                                    Claim Bonus
                                </a>
                            </div>
                        </div>
                    )}
                </div>
            </div>
        </div>
    );
}

export default Intro;
