import React, { Component } from 'react'

export class Item extends Component {
    render() {
        return (
            <div className='card'>
                <picture className='card-logo'>
                    <source srcSet={'./img/casino-logo/' + this.props.item.imgAdditional} type='image/png' />
                    <img src={'./img/casino-logo/' + this.props.item.img} alt='logo' />
                </picture>
                <div className='card-text'>
                    <div className='card-title'>{this.props.item.title}</div>
                    <div className='card-subtitle'>{this.props.item.subtitle}</div>
                    <div className='card-description'>{this.props.item.description}</div>
                </div>
                <a className='btn btn-card btn-small bg-violet' href={this.props.item.link} target='_self' type='button'>Claim bonus</a>
            </div>

        )
    }
}

export default Item
