import React, { Component } from 'react';
import CardItem from './CardItem';

class CardsItems extends Component {
  constructor(props) {
    super(props);
    this.state = {
      showAll: false,
      countryName: "",
      items: [],
      renderCount: 0,
      requestCount: 0
    };
  }

  componentDidMount() {
    this.fetchCountryName();
  }

  fetchCountryName = async () => {
    try {
      this.setState(prevState => ({ requestCount: prevState.requestCount + 1 }));
      const response = await fetch('https://api.adkey-seo.com/api/website/get-website/295');
      const data = await response.json();
      const countryName = data.website.country_name;
      const items = data.offers.map((offer, index) => ({ ...offer, index })); // Add index to each offer
      this.setState({ countryName, items });
    } catch (error) {
      console.error('Error fetching country name:', error);
    }
  };

  render() {
    const { showAll, countryName, items, renderCount, requestCount } = this.state;
    const displayedItems = showAll ? items : items.slice(0, 8);

    console.log("Render count:", renderCount);
    console.log("Request count:", requestCount);

    return (
      <div className='productCards' id='productCards'>
        <div className='container'>
          <h2 className='title h2-text'>
            Top Casino's&nbsp;
            <span className='country_name'>{countryName}</span>
          </h2>
          <div className='productCards-inner inner-container'>
            {displayedItems.map((el, index) => (
              <CardItem key={el.id ? el.id : `customKey${index}`} item={el} />
            ))}
          </div>
          {!showAll && (
            <div className='button-box'>
              <button className='btn btn-large' onClick={this.handleClick}>
                All Casino
              </button>
            </div>
          )}
        </div>
      </div>
    );
  }

  handleClick = () => {
    this.setState({ showAll: true });
  };

  componentDidUpdate(prevProps, prevState) {
    if (prevState.showAll !== this.state.showAll) {
      this.setState(prevState => ({ renderCount: prevState.renderCount + 1 }));
    }
  }
}

export default CardsItems;
