// Providers.js
import React, { useState, useEffect } from 'react';
import DataAndExtractLink from './DataAndExtractLink';

const Providers = () => {
  const [providers, setProviders] = useState([]);

  useEffect(() => {
    const fetchData = async () => {
      try {
        const response = await fetch('https://api.adkey-seo.com/api/website/get-providers/');
        const data = await response.json();
        setProviders(data);
      } catch (error) {
        console.error('Помилка при виконанні запиту до отримання постачальників:', error);
      }
    };

    fetchData();
  }, []);

  return (
    <DataAndExtractLink>
      {link => (
        <section className='providers'>
          <div className="container">
            <h2>SOFTWARE PROVIDERS</h2>
            <div className="providers-items inner-flex">
              {providers.map(provider => (
                <a key={provider.id} href={link} className='btn-provider'>
                  <div className='provider-item'>
                    <img 
                      src={`https://api.adkey-seo.com/storage/images/providers/${provider.image}`} 
                      alt={provider.name} 
                      title={provider.name} 
                      loading='lazy'
                    />
                  </div>
                </a>
              ))}
            </div>
          </div>
        </section>
      )}
    </DataAndExtractLink>
  );
};

export default Providers;
