import React, { useState, useEffect } from 'react';
import CardItem from './CardItem';
import MiddlePage from './MiddlePage';
import BtnLinkHandler from './BtnLinkHandler';

const CardsItems = () => {
  const [data, setData] = useState(null);
  const [displayedOffers, setDisplayedOffers] = useState([]);
  const [showContent, setShowContent] = useState(false); // Якщо вам потрібно для BtnLinkHandler

  useEffect(() => {
    const fetchData = async () => {
      try {
        const response = await fetch('https://api.adkey-seo.com/api/website/get-website/295');
        const responseData = await response.json();
        setData(responseData);
        setDisplayedOffers(responseData.offers.slice(0, 8));
      } catch (error) {
        console.error('Error fetching data:', error);
      }
    };

    fetchData();
  }, []);

  const handleClick = () => {
    setDisplayedOffers(data.offers);
  };

  return (
    <div className='productCards' id='productCards'>
      <div className='container'>
        <h2 className='title h2-text'>
          Top Casino's&nbsp;
          <span className='country_name'>{data?.website.country_name}</span>
        </h2>
        {/* Додано компонент MiddlePage, якщо showContent є false */}
        {!showContent && <MiddlePage />}
        <div className='productCards-inner inner-container'>
          {displayedOffers.map(offer => (
            <CardItem key={offer.id} offer={offer} />
          ))}
        </div>
        {/* Показ кнопки лише якщо відображено лише часткову кількість карток */}
        {displayedOffers.length < data?.offers.length && (
          <div className='button-box'>
            <button className='btn btn-large' onClick={handleClick}>
              All Casino
            </button>
          </div>
        )}
        {/* Додано BtnLinkHandler */}
        <BtnLinkHandler setShowContent={setShowContent} />
      </div>
    </div>
  );
};

export default CardsItems;
