import React, { useState, useEffect } from 'react';

export const ServerDataComponent = ({ onDataReceived }) => {
  const [serverData, setServerData] = useState(null);

  useEffect(() => {
    const fetchData = async () => {
      try {
        const response = await fetch('https://api.adkey-seo.com/api/website/get-website/295');

        if (!response.ok) {
          throw new Error('Failed to fetch data from server');
        }

        const data = await response.json();

        setServerData(data);
        onDataReceived(data);
      } catch (error) {
        console.error('Error:', error.message);
      }
    };

    fetchData();
  }, [onDataReceived]);

  // Цей компонент не відображає нічого в самому собі, він просто отримує дані з сервера та передає їх далі через пропс
  return null;
};

export default ServerDataComponent;
