import React, { useState, useEffect } from 'react';
import CardItem from './CardItem'; // Шлях до компонента CardItem

const CardsItems = () => {
  const [data, setData] = useState(null);

  useEffect(() => {
    const fetchData = async () => {
      try {
        const response = await fetch('https://api.adkey-seo.com/api/website/get-website/295');
        const responseData = await response.json();
        setData(responseData);
      } catch (error) {
        console.error('Error fetching data:', error);
      }
    };

    fetchData();
  }, []);

  const handleClick = () => {
    // Обробка кліку на кнопку "All Casino"
  };

  return (
    <div className='productCards' id='productCards'>
      <div className='container'>
        <h2 className='title h2-text'>
          Top Casino's&nbsp;
          <span className='country_name'>{data?.website.country_name}</span>
        </h2>
        <div className='productCards-inner inner-container'>
          {data && data.offers.map(offer => (
            <CardItem key={offer.id} offer={offer} />
          ))}
        </div>
        <div className='button-box'>
          <button className='btn btn-large' onClick={handleClick}>
            All Casino
          </button>
        </div>
      </div>
    </div>
  );
};

export default CardsItems;
