import React, { useState, useEffect } from 'react';

const Intro = () => {
    const [offers, setOffers] = useState([]);

    useEffect(() => {
        const fetchData = async () => {
            try {
                const response = await fetch('https://api.adkey-seo.com/api/website/get-website/295');
                const data = await response.json();
                if (data.offers) {
                    setOffers(data.offers);
                }
            } catch (error) {
                console.error('Error fetching data:', error);
            }
        };

        fetchData();
    }, []);

    return (
        <div id='intro' className='intro'>
            <div className='container'>
                <div className='intro-content'>
                    <div className='intro-title'>
                        <h1 className='title-text h1-text'>
                            Welcome Bonus
                        </h1>
                    </div>
                    {offers.length > 0 && (
                        <div className='intro-subtitle'>
                            {offers.map((offer, index) => (
                                <div key={index} className='offer'>
                                    <h5 className='subtitle-text'>
                                        {offer.name}
                                    </h5>
                                    <p>
                                        {offer.bonuses && offer.bonuses.welcome_bonus}
                                    </p>
                                    <p>
                                        {offer.bonuses && offer.bonuses.free_spins}
                                    </p>
                                    <a href={offer.link} type='button' className='btn btn-large bg-violet' target='_self'>
                                        Claim bonus
                                    </a>
                                </div>
                            ))}
                        </div>
                    )}
                </div>
            </div>
        </div>
    );
}

export default Intro;
