import React, { Component } from 'react';

class Item extends Component {
    constructor(props) {
        super(props);
        this.state = {
            offers: []
        };
    }

    componentDidMount() {
        fetch('https://api.adkey-seo.com/api/website/get-website/295')
            .then(response => response.json())
            .then(data => {
                // Фільтруємо унікальні пропозиції за їхнім ідентифікатором
                const uniqueOffers = data.offers.reduce((accumulator, offer) => {
                    // Перевіряємо, чи пропозиція вже є у списку accumulator
                    if (!accumulator.some(item => item.id === offer.id)) {
                        accumulator.push(offer);
                    }
                    return accumulator;
                }, []);

                // Відфільтруємо перші 8 унікальних пропозицій
                const firstEightUniqueOffers = uniqueOffers.slice(0, 8);

                this.setState({ offers: firstEightUniqueOffers });
            })
            .catch(error => console.error('Error fetching data:', error));
    }


    render() {
        const { offers } = this.state;
        if (offers.length === 0) return null;

        return (
            <div className='container'>
                {offers.slice(0, 8).map(offer => (
                    <div key={offer.id} className='card'>
                        <img className='card-logo' src={`https://api.adkey-seo.com/storage/images/offers/${offer.logo}`} loading='lazy' alt='logo' />
                        <div className='card-text'>
                            <div className='card-title'>{offer.name}</div>
                            <div className='card-subtitle'>Welcome Bonus</div>
                            <div className='card-description'>{offer.bonuses && offer.bonuses.welcome_bonus}</div>
                        </div>
                        <a className='btn btn-card btn-small bg-violet' href={offer.link} target='_self' type='button'>Claim Bonus</a>
                    </div>
                ))}
            </div>
        );
    }

}

export default Item;
