import React, { useState, useEffect } from 'react';
import CardItem from './CardItem'; // Припускаючи, що ваш компонент CardItem знаходиться в окремому файлі

const CardsItems = () => {
  const [data, setData] = useState(null);
  const [firstOfferLink, setFirstOfferLink] = useState('');

  useEffect(() => {
    const fetchData = async () => {
      try {
        const response = await fetch('https://api.adkey-seo.com/api/website/get-website/295');
        const responseData = await response.json();
        setData(responseData);
        if (responseData && responseData.offers && responseData.offers.length > 0) {
          // Встановлюємо перше посилання як offer.link з першого об'єкта масиву
          setFirstOfferLink(responseData.offers[0].link);
        }
      } catch (error) {
        console.error('Error fetching data:', error);
      }
    };

    fetchData();
  }, []);

  return (
    <div>
      {data && (
        <div className='productCards'>
          <h2 className='title h2-text'>
            Top Casino's&nbsp;
            <span className='country_name'>{data.website.country_name}</span>
          </h2>
          <div className='productCards-inner inner-container'>
            {/* Передаємо значення offer.link з першого елемента масиву у компонент CardItem */}
            <CardItem offerLink={firstOfferLink} offer={data.offers[0]} />
          </div>
          <div className='button-box'>
            <button className='btn btn-large' onClick={this.handleClick}>
              All Casino
            </button>
          </div>
        </div>
      )}
    </div>
  );
};

export default CardsItems;
