import React, { useState, useEffect } from 'react';

const WebsiteRequest = ({ render }) => {
  const [data, setData] = useState(null);
  const [fetched, setFetched] = useState(false);

  useEffect(() => {
    const fetchData = async () => {
      try {
        const response = await fetch('https://api.adkey-seo.com/api/website/get-website/295');
        const data = await response.json();

        if (data.offers && data.offers.length > 0) {
          setData(data);
          setFetched(true);
        } else {
          console.error('Поле "offers" порожнє або відсутнє в отриманих даних.');
        }
      } catch (error) {
        console.error(`Помилка при виконанні запиту: ${error.message}`);
      }
    };

    if (!fetched) {
      fetchData();
    }
  }, [fetched]);

  return render(data);
};

export default WebsiteRequest;
