import React, { useState, useEffect } from 'react';

const Intro = () => {
    const [websiteData, setWebsiteData] = useState(null);

    useEffect(() => {
        const fetchData = async () => {
            try {
                const response = await fetch('https://api.adkey-seo.com/api/website/get-website/295');
                const data = await response.json();
                setWebsiteData(data.website);
            } catch (error) {
                console.error('Error fetching data:', error);
            }
        };

        fetchData();
    }, []);

    return (
        <div id='intro' className='intro'>
            <div className='container'>
                <div className='intro-content'>
                    <div className='intro-title'>
                        <h1 className='title-text h1-text'>
                            Welcome Bonus
                        </h1>
                    </div>
                    {websiteData && (
                        <div className='intro-subtitle'>
                            <h5 className='subtitle-text'>
                                EXCLUSIVE WELCOME OFFER OF
                                <span className='welcome_bonus'>
                                    {websiteData.bonuses && websiteData.bonuses.welcome_bonus}
                                </span>
                                <span className='free_spins'>
                                    {websiteData.bonuses && websiteData.bonuses.free_spins}
                                </span>
                            </h5>
                        </div>
                    )}
                    <div className='intro-button'>
                        <a href={websiteData ? websiteData.link : '#'} type='button' className='btn btn-large bg-violet' target='_self'>
                            Claim bonus
                        </a>
                    </div>
                </div>
            </div>
        </div>
    );
}

export default Intro;
