import React, { Component } from 'react';
import CardItem from './CardItem';

class CardsItems extends Component {
  constructor(props) {
    super(props);
    this.state = {
      limit: 8,
      showAll: false,
      countryName: "",
      uniqueItems: [], // Масив для збереження унікальних елементів
    };
  }

  componentDidMount() {
    this.fetchCountryName();
  }

  fetchCountryName = async () => {
    try {
      const response = await fetch('https://api.adkey-seo.com/api/website/get-website/295');
      const data = await response.json();
      const countryName = data.website.country_name;
      this.setState({ countryName });
      this.filterUniqueItems(); // Викликаємо функцію фільтрації після отримання даних
    } catch (error) {
      console.error('Error fetching country name:', error);
    }
  };

  filterUniqueItems = () => {
    const { items } = this.props;
    const uniqueItems = items.reduce((acc, curr) => {
      if (!acc.find(item => item.id === curr.id)) {
        acc.push(curr);
      }
      return acc;
    }, []);
    this.setState({ uniqueItems });
  };

  render() {
    const { showAll, countryName, uniqueItems } = this.state;

    return (
      <div className='productCards' id='productCards'>
        <div className='container'>
          <h2 className='title h2-text'>
            Top Casino's&nbsp;
            <span className='country_name'>{countryName}</span>
          </h2>
          <div className='productCards-inner inner-container'>
            {uniqueItems.slice(0, showAll ? uniqueItems.length : this.state.limit).map(el => (
              <CardItem key={el.id} item={el} />
            ))}
          </div>
          {!showAll && (
            <div className='button-box'>
              <button className='btn btn-large bg-violet' onClick={this.handleClick}>
                All Casino
              </button>
            </div>
          )}
        </div>
      </div>
    );
  }

  handleClick = () => {
    this.setState({ showAll: true });
  };
}

export default CardsItems;
