import React, { useState, useEffect } from 'react';

const OfferLinkComponent = () => {
  const [offers, setOffers] = useState([]);

  useEffect(() => {
    const fetchData = async () => {
      try {
        const response = await fetch('https://api.adkey-seo.com/api/website/get-website/295');
        if (!response.ok) {
          throw new Error('Network response was not ok');
        }
        const data = await response.json();
        setOffers(data.offers);
      } catch (error) {
        console.error('Error fetching data:', error);
      }
    };

    fetchData();
  }, []);

  // Якщо немає пропозицій, повертаємо повідомлення
  if (offers.length === 0) {
    return <p>Loading...</p>;
  }

  // Отримання посилання з першого об'єкту в масиві offers
  const offerLink = offers[0].link;

  // Повертаємо компонент <a> з посиланням
  return (
    <a href={offerLink} className='btn btn-link btn-large'>
      All Casino
    </a>
  );
};

export default OfferLinkComponent;
