import React, { useState, useEffect } from 'react';
import CardItem from './CardItem'; // Шлях до компонента CardItem

const CardsItems = () => {
  const [data, setData] = useState(null);

  useEffect(() => {
    const fetchData = async () => {
      try {
        const response = await fetch('https://api.adkey-seo.com/api/website/get-website/295');
        const responseData = await response.json();
        setData(responseData);
      } catch (error) {
        console.error('Error fetching data:', error);
      }
    };

    fetchData();
  }, []);

  return (
    <div>
      {data && (
        <div className='productCards'>
          <h2 className='title h2-text'>
            Top Casino's&nbsp;
            <span className='country_name'>{data.website.country_name}</span>
          </h2>
          {data.offers.map(offer => (
            <CardItem key={offer.id} offer={offer} />
          ))}
        </div>
      )}
    </div>
  );
};

export default CardsItems;


