import React from 'react';

const CardItem = ({ item }) => {
  return (
    <div className='card'>
      <picture className='card-logo'>
        <source srcSet={'./img/casino-logo/' + item.imgAdditional} type='image/png' />
        <img src={'./img/casino-logo/' + item.img} alt='logo' />
      </picture>
      <div className='card-text'>
        <div className='card-title'>{item.title}</div>
        <div className='card-subtitle'>{item.subtitle}</div>
        <div className='card-description'>{item.description}</div>
      </div>
      <a className='btn btn-card btn-small bg-violet' href={item.link} target='_self' type='button'>Claim bonus</a>
    </div>
  );
};

export default CardItem;
