import React, { useState, useEffect } from 'react';
import GameItem from './GameItem'; // Припустимо, що файл GameItem.js містить компонент GameItem

const Games = () => {
  const [games, setGames] = useState([]);

  useEffect(() => {
    const fetchData = async () => {
      try {
        const response = await fetch('https://api.adkey-seo.com/api/website/get-games/gambling');
        if (!response.ok) {
          throw new Error('Network response was not ok');
        }
        const data = await response.json();
        setGames(data);
      } catch (error) {
        console.error('There was a problem fetching the data:', error);
      }
    };

    fetchData();
  }, []);

  return (
    <div className='games' id='games'>
      <div className="container">
        <h2 className="title h2-text">
          Top games
        </h2>
        <div className="games-items">
          {games.map(game => (
            <GameItem key={game.id} id={game.id} name={game.name} image={game.image}/>
          ))}
        </div>
      </div>
    </div>
  );
};

export default Games;
