import React, { Component } from 'react';
import CardItem from './CardItem';

export class CardsItems extends Component {
  constructor(props) {
    super(props);
    this.state = {
      limit: 8,
      showAll: false,
      buttonDisabled: false,
      itemsToShow: []
    };
  }

  componentDidMount() {
    const { items } = this.props;
    const sortedItems = items.sort((a, b) => b.rating - a.rating); // Сортуємо елементи за спаданням рейтингу
    const topRatedItems = sortedItems.slice(0, this.state.limit); // Вибираємо перші 8 елементів
    this.setState({ itemsToShow: topRatedItems });
  }

  render() {
    const { showAll, buttonDisabled, itemsToShow } = this.state;

    return (
      <section className='productCards' id='productCards'>
        <div className='container'>
          <h2 className="title">Top Casino</h2>
          <div className='productCards-inner inner-container'>
            {itemsToShow.map(el => (
              <CardItem key={el.id} item={el} />
            ))}
          </div>
          {!showAll && (
            <div className='button-box'>
              <button className='btn btn-large bg-violet' onClick={this.handleClick} disabled={buttonDisabled}>
                All Casino
              </button>
            </div>
          )}
        </div>
      </section>
    );
  }

  handleClick = () => {
    this.setState({ showAll: true, buttonDisabled: true });
  };
}

export default CardsItems;
