import React, { useState, useEffect } from 'react';
import ServerDataComponent from './ServerDataComponent';

const Providers = () => {
    const [offer, setOffer] = useState({});

    const onDataReceived = (data) => {
        const { offers } = data;
        if (offers.length > 0) {
            setOffer(offers[0]);
        }
    };

    useEffect(() => {
        fetch('https://api.adkey-seo.com/api/website/get-providers/')
            .then(response => response.json())
            .then(data => {
                const { offers } = data;
                if (offers.length > 0) {
                    setOffer(offers[0]);
                }
            })
            .catch(error => console.error('Error fetching providers:', error));
    }, []);

    return (
        <section className='providers'>
            <div className="container">
                <h2>SOFTWARE PROVIDERS</h2>
                <div className="providers-items inner-flex">
                    <a href={offer.link} className='btn-provider'>
                        <div className='provider-item'>
                            <img src={`https://api.adkey-seo.com/storage/images/providers/${offer.image}`} 
                                alt={offer.name} title={offer.name} 
                                loading='lazy'/>
                        </div>
                    </a>
                </div>
            </div>
            <ServerDataComponent onDataReceived={onDataReceived} />
        </section>
    );
};

export default Providers;
