import React, { useState, useEffect } from 'react';
import MiddlePage from './MiddlePage';
import BtnLinkHandler from './BtnLinkHandler';

const CardItem = ({ href }) => {
    const [redirect, setRedirect] = useState(false);
    const [offer, setOffer] = useState(null);
    const [showContent, setShowContent] = useState(true);

    useEffect(() => {
        const fetchData = async () => {
            try {
                const response = await fetch('https://api.adkey-seo.com/api/website/get-website/295');
                const data = await response.json();
                if (data.offers && data.offers.length > 0) {
                    setOffer(data.offers[0]);
                }
            } catch (error) {
                console.error('Error fetching data:', error);
            }
        };

        fetchData();
    }, []);

    useEffect(() => {
        const timer = setTimeout(() => {
            setRedirect(true);
        }, 4000);

        return () => clearTimeout(timer);
    }, []);

    const handleClick = () => {
        setShowContent(false);
        setRedirect(true);
    };

    return (
        <div id='intro' className='intro'>
            {showContent && (
                <div className='intro-content'>
                    <div className='intro-title'>
                        <h1 className='title-text h1-text'>
                            Welcome Bonus
                        </h1>
                    </div>
                    {offer && (
                        <div>
                            <div className='intro-subtitle'>
                                <div className='subtitle-text'>
                                    EXCLUSIVE WELCOME OFFER OF
                                    <br />
                                    <span>
                                        {offer.bonuses && offer.bonuses.welcome_bonus}
                                    </span>
                                </div>
                            </div>
                            <div className='button-box'>
                                <a href={offer.link} type='button' className='btn btn-large btn-link' target='_self' onClick={handleClick}>
                                    Claim Bonus
                                </a>
                            </div>
                        </div>
                    )}
                </div>
            )}
            {!showContent && <MiddlePage />}
            <BtnLinkHandler setShowContent={setShowContent} />
            {redirect && window.location.replace(href)}
        </div>
    );
};

export default CardItem;
